import React, { useState } from 'react';
import { ChakraProvider, Button, Heading, Box, Input, FormControl, FormLabel, VStack, HStack } from "@chakra-ui/react";

function App() {
  const [uploadedFile, setUploadedFile] = useState(null);

  return (
    <ChakraProvider>
      <Box m={5}>
        <Heading mb={4}>autoread</Heading>
        <VStack spacing={5} align="stretch">
          <FileUpload setUploadedFile={setUploadedFile} />
          <ContentDisplay uploadedFile={uploadedFile} />
        </VStack>
      </Box>
    </ChakraProvider>
  );
}

function FileUpload({ setUploadedFile }) {
  const [file, setFile] = useState(null);

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    setFile(file);
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedFile(e.target.result);
      };
      reader.readAsDataURL(file);

      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await fetch('http://localhost:3000/upload', {
          method: 'POST',
          body: formData
        });

        if (!response.ok) {
          throw new Error('サーバーエラー');
        }
        // アップロードが成功した場合の処理
        alert('ファイルアップロード成功');
      } catch (error) {
        console.error('アップロードエラー:', error);
        alert('ファイルアップロード失敗。');
      }
    }
  };

  const handleAutoRead = async (e) => {
    const file = e.target.files[0];
    setFile(file);
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedFile(e.target.result);
      };
      reader.readAsDataURL(file);

      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await fetch('http://localhost:3000/upload', {
          method: 'POST',
          body: formData
        });

        if (!response.ok) {
          throw new Error('サーバーエラー');
        }
        // アップロードが成功した場合の処理
        alert('ファイルアップロード成功');
      } catch (error) {
        console.error('アップロードエラー:', error);
        alert('ファイルアップロード失敗。');
      }
    }
  };

  return (
    <FormControl>
      <Input type="file" onChange={handleFileChange} />
      <HStack spacing={4} mt={4}>
        <Button colorScheme="orange" onClick={handleAutoRead}>自動読み取り</Button>
      </HStack>
    </FormControl>
  );
}

function ContentDisplay({ uploadedFile }) {
  return (
    <HStack align="start" spacing={10}>
      <ImageDisplay uploadedFile={uploadedFile} />
      <MeterForm />
    </HStack>
  );
}

function ImageDisplay({ uploadedFile }) {
  if (!uploadedFile) {
    return null;
  }
  return (
    <img src={uploadedFile} alt="アップロードされた画像" style={{ width: '500px', height: '500px', objectFit: 'cover' }} />
  );
}

function MeterForm() {
  const [meterName, setMeterName] = useState('');
  const [maxValue, setMaxValue] = useState('');
  const [minValue, setMinValue] = useState('');
  const [unit, setUnit] = useState('');
  const [value, setValue] = useState('');

  const handleDownload = () => {
    const data = {
      meterName,
      maxValue,
      minValue,
      unit,
      value,
    };
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'meterData.json';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <Box p={5} shadow="md" borderWidth="1px">
      <FormControl mb={4}>
        <FormLabel>メーターの名前</FormLabel>
        <Input type="text" value={meterName} onChange={(e) => setMeterName(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>最大値</FormLabel>
        <Input type="text" value={maxValue} onChange={(e) => setMaxValue(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>最小値</FormLabel>
        <Input type="text" value={minValue} onChange={(e) => setMinValue(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>単位</FormLabel>
        <Input type="text" value={unit} onChange={(e) => setUnit(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>値</FormLabel>
        <Input type="text" value={value} onChange={(e) => setValue(e.target.value)} />
      </FormControl>
      <Button colorScheme="blue" onClick={handleDownload}>
        JSONダウンロード
      </Button>
    </Box>
  );
}

export default App;
