import React from 'react';
import { ChakraProvider, Button, Heading, Box } from "@chakra-ui/react";

function App() {
  return (
    <ChakraProvider>
      <Box m={5}>
        <Heading mb={4}>autoread</Heading>
        <FileUpload />
        <ImageDisplay />
      </Box>
    </ChakraProvider>
  );
}

function FileUpload() {
  const [file, setFile] = React.useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('ファイルを選択してください。');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:3000/upload', {
        method: 'POST',
        body: formData //アップロード画像
      });

      if (!response.ok) {
        throw new Error('サーバーエラー');
      }
      // レスポンスがOKの場合、ページを再読み込み
      window.location.reload();
    } catch (error) {
      console.error('アップロードエラー:', error);
      alert('ファイルアップロード失敗。');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="file" onChange={handleFileChange} />
      <Button mt={4} colorScheme="teal" type="submit">アップロード</Button>
    </form>
  );
}

function ImageDisplay() {
  // 直接画像パスを指定
  const imageUrl = "processed/processed_image.jpg";
  return <img src={imageUrl} alt="アップロードされた画像" />;
}

export default App;
