import React, { useState } from 'react';
import { ChakraProvider, Button, Heading, Box, Input, FormControl, FormLabel, Image, VStack } from "@chakra-ui/react";

function App() {
  return (
    <ChakraProvider>
      <Box m={5}>
        <Heading mb={4}>autoread</Heading>
        <VStack spacing={5} align="stretch">
          <FileUpload />
          <ImageDisplay />
          <MeterForm />
        </VStack>
      </Box>
    </ChakraProvider>
  );
}

function FileUpload() {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('ファイルを選択してください。');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:3000/upload', {
        method: 'POST',
        body: formData //アップロード画像
      });

      if (!response.ok) {
        throw new Error('サーバーエラー');
      }
      // レスポンスがOKの場合、ページを再読み込み
      window.location.reload();
    } catch (error) {
      console.error('アップロードエラー:', error);
      alert('ファイルアップロード失敗。');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <FormControl>
        <Input type="file" onChange={handleFileChange} />
        <Button mt={4} colorScheme="teal" type="submit">アップロード</Button>
      </FormControl>
    </form>
  );
}

function ImageDisplay() {
  // 直接画像パスを指定
  const imageUrl = "processed/processed_image.jpg";
  return <Image src={imageUrl} alt="アップロードされた画像" />;
}

function MeterForm() {
  const [meterName, setMeterName] = useState('');
  const [maxValue, setMaxValue] = useState('');
  const [minValue, setMinValue] = useState('');
  const [unit, setUnit] = useState('');
  const [value, setValue] = useState('');

  const handleDownload = () => {
    const data = {
      meterName,
      maxValue,
      minValue,
      unit,
      value,
    };
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'meterData.json';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <Box p={5} shadow="md" borderWidth="1px">
      <FormControl mb={4}>
        <FormLabel>メーターの名前</FormLabel>
        <Input type="text" value={meterName} onChange={(e) => setMeterName(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>最大値</FormLabel>
        <Input type="text" value={maxValue} onChange={(e) => setMaxValue(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>最小値</FormLabel>
        <Input type="text" value={minValue} onChange={(e) => setMinValue(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>単位</FormLabel>
        <Input type="text" value={unit} onChange={(e) => setUnit(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>値</FormLabel>
        <Input type="text" value={value} onChange={(e) => setValue(e.target.value)} />
      </FormControl>
      <Button colorScheme="blue" onClick={handleDownload}>
        JSONダウンロード
      </Button>
    </Box>
  );
}

export default App;
