import React, { useState } from 'react';
import { ChakraProvider, Button, Heading, Box, Input, FormControl, FormLabel, VStack, HStack, Progress } from "@chakra-ui/react";
import axios from 'axios';  // Axiosのインポート

function App() {
  const [uploadedFile, setUploadedFile] = useState(null);
  const [file, setFile] = useState(null);
  const [meterData, setMeterData] = useState({ maxValue: '', minValue: '' });
  const [tempMaxValue, setTempMaxValue] = useState(''); // 一時的な最大値
  const [tempMinValue, setTempMinValue] = useState(''); // 一時的な最小値
  const [value, setValue] = useState(''); // "値" フィールドの状態を管理する
  const [progress, setProgress] = useState(0); // プログレスバーの進行状況を管理

  return (
    <ChakraProvider>
      <Box m={5}>
        <Heading mb={4}>autoread</Heading>
        <VStack spacing={5} align="stretch">
          <FileUpload setUploadedFile={setUploadedFile} setFile={setFile} />
          <ContentDisplay 
            uploadedFile={uploadedFile} 
            file={file} 
            meterData={meterData} 
            setMeterData={setMeterData}
            tempMaxValue={tempMaxValue}
            setTempMaxValue={setTempMaxValue}
            tempMinValue={tempMinValue}
            setTempMinValue={setTempMinValue}
            value={value} // "値" フィールドの値を渡す
            setValue={setValue} // "値" フィールドを更新するための関数を渡す
            progress={progress} // プログレスバーの進行状況を渡す
            setProgress={setProgress} // プログレスバーを更新するための関数を渡す
          />
        </VStack>
      </Box>
    </ChakraProvider>
  );
}

function FileUpload({ setUploadedFile, setFile }) {
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFile(file);
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedFile(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <FormControl>
      <Input type="file" onChange={handleFileChange} />
    </FormControl>
  );
}

function ContentDisplay({ uploadedFile, file, meterData, setMeterData, tempMaxValue, setTempMaxValue, tempMinValue, setTempMinValue, value, setValue, progress, setProgress }) {
  let simulatedProgress = 0; // シミュレーション用の進行状況

  const handleAutoRead = async () => {
    if (!file) {
      alert('ファイルを選択してください。');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('maxValue', tempMaxValue); 
    formData.append('minValue', tempMinValue); 

    try {
      setProgress(0); // プログレスバーのリセット

      // Axiosでファイルをアップロードし、進行状況を監視
      const response = await axios.post('http://localhost:3000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          if (progressEvent.lengthComputable) {
            // 実際の進行状況
            const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);

            // 進行状況のシミュレーションを追加
            if (simulatedProgress < percentCompleted) {
              simulatedProgress += 5; // シミュレーションの進行速度を遅くするために1%ずつ進める
            }

            setTimeout(() => {
              setProgress(simulatedProgress); // 擬似的に遅く進める
            }, 50); // 100ミリ秒の遅延を追加して進行をゆっくりに見せる
          }
        }
      });

      const result = response.data;
      setMeterData({
        maxValue: result.End || '',
        minValue: result.Start || ''
      });
      setValue(parseFloat(result.result_value).toFixed(3));

      alert('ファイルアップロード成功');
    } catch (error) {
      console.error('アップロードエラー:', error);
      alert('ファイルアップロード失敗。');
    }
  };
  return (
    <HStack align="start" spacing={10}>
      <ImageDisplay uploadedFile={uploadedFile} />
      <MeterForm 
        handleAutoRead={handleAutoRead} 
        tempMaxValue={tempMaxValue} 
        setTempMaxValue={setTempMaxValue} 
        tempMinValue={tempMinValue} 
        setTempMinValue={setTempMinValue} 
        value={value} // "値" フィールドの値を渡す
        setValue={setValue} // "値" フィールドを更新するための関数を渡す
        progress={progress} // プログレスバーの値を渡す
      />
    </HStack>
  );
}

function ImageDisplay({ uploadedFile }) {
  if (!uploadedFile) {
    return null;
  }
  return (
    <img src={uploadedFile} alt="アップロードされた画像" style={{ width: '500px', height: '500px', objectFit: 'cover' }} />
  );
}

function MeterForm({ handleAutoRead, tempMaxValue, setTempMaxValue, tempMinValue, setTempMinValue, value, setValue, progress }) {
  const [meterName, setMeterName] = useState('');
  const [unit, setUnit] = useState('');

  const handleDownload = () => {
    const data = {
      meterName,
      maxValue: tempMaxValue,
      minValue: tempMinValue,
      unit,
      value,
    };
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'meterData.json';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <Box p={5} shadow="md" borderWidth="1px">
      <FormControl mb={4}>
        <FormLabel>メーターの名前</FormLabel>
        <Input type="text" value={meterName} onChange={(e) => setMeterName(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>最大値</FormLabel>
        <Input
          type="text"
          value={tempMaxValue}
          onChange={(e) => setTempMaxValue(e.target.value)}
        />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>最小値</FormLabel>
        <Input
          type="text"
          value={tempMinValue}
          onChange={(e) => setTempMinValue(e.target.value)}
        />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>単位</FormLabel>
        <Input type="text" value={unit} onChange={(e) => setUnit(e.target.value)} />
      </FormControl>
      <FormControl mb={4}>
        <FormLabel>値</FormLabel>
        <Input type="text" value={value} onChange={(e) => setValue(e.target.value)} />
      </FormControl>
      <Progress value={progress} size="sm" colorScheme="green" mb={4} />
      <Button colorScheme="blue" onClick={handleDownload}>
        JSONダウンロード
      </Button>
      <Button colorScheme="orange" onClick={handleAutoRead}>
        自動読み取り
      </Button>
    </Box>
  );
}

export default App;
